generated.css:
.github {
  overflow-x: auto;
  max-width: 980px;
  width: calc(100vw - 50px);
  margin: 1rem auto;
  background-color: #fff;
  padding: 45px;
  min-height: 100%;
  flex: 1;
}
@media screen and (max-width: 768px) {
  .github {
    width: 100vw;
    padding: 24px;
  }
}
.github .title {
  font-size: 1.5em;
  margin: 10px 0 5px;
}
.github .calendar {
  overflow-x: auto;
  width: 100%;
  font-family: Helvetica, arial;
  border: 1px solid #DDDDDD;
  border-radius: 3px;
  min-height: 243px;
  text-align: center;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  grid-template-rows: 40px 3fr 2fr;
  background-color: #DDDDDD;
  grid-column-gap: 1px;
}
.github .calendar .text-normal {
  width: 888px;
  grid-column: 1 / 4;
  background-color: #fff;
}
.github .calendar .border-gray-dark {
  width: 888px;
  grid-column: 1 / 4;
  background-color: #fff;
}
.github .calendar .border-gray-dark svg {
  font-size: 10px;
  fill: #aaa;
}
.github .calendar .border-gray-dark .contrib-footer {
  grid-row: 0.33333333;
  border-bottom: 1px solid #DDDDDD;
  font-size: 11px;
  padding: 0 10px 12px;
  text-align: left;
  width: 100%;
  box-sizing: border-box;
  height: 26px;
  display: inline-flex;
  justify-content: space-between;
}
.github .calendar .border-gray-dark .contrib-footer:before,
.github .calendar .border-gray-dark .contrib-footer:after {
  content: none;
}
.github .calendar .border-gray-dark .contrib-footer .contrib-legend {
  display: inline-flex;
  flex-direction: row;
  width: 126.66px;
  justify-content: space-between;
}
.github .calendar .border-gray-dark .contrib-footer .contrib-legend .legend {
  width: 66px;
  display: inline-flex;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
}
.github .calendar .border-gray-dark .contrib-footer .contrib-legend .legend li {
  width: 11px;
  height: 11px;
}
.github .calendar .contrib-column {
  width: 295px;
  background-color: #fff;
  display: inline-flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 15px 0;
}
.github .calendar .contrib-column .text-muted {
  font-size: 11px;
}
.github .calendar .contrib-column .contrib-number {
  line-height: 1.3em;
  font-size: 24px;
  display: block;
  color: #333;
}
.github .basic {
  overflow-x: auto;
  width: 100%;
  font-family: Helvetica, arial;
  border: 1px solid #DDDDDD;
  border-radius: 3px;
  min-height: 243px;
  text-align: center;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  grid-template-rows: 2fr 1fr;
  grid-column-gap: 1px;
}
.github .basic .detail {
  display: flex;
  align-items: center;
  grid-column: 1 / 4;
  flex-direction: row;
  border-bottom: 1px solid #DDDDDD;
}
.github .basic .detail img {
  width: 100px;
  height: 100px;
  margin: 30px;
  border-radius: 100px;
}
.github .basic .detail .detail-container {
  height: 160px;
  display: inline-flex;
  flex-direction: column;
  justify-content: space-evenly;
  align-items: flex-start;
}
.github .basic .repositories,
.github .basic .followers,
.github .basic .following {
  background-color: green;
  width: 80%;
  margin: 1rem auto;
  text-align: center;
  color: #ffffff;
  padding: 2rem;
}
@media screen and (max-width: 768px) {
  .github .basic .repositories,
  .github .basic .followers,
  .github .basic .following {
    grid-column: 1 / 4;
  }
}
.github .basic .repositories .num,
.github .basic .followers .num,
.github .basic .following .num {
  font-size: 2em;
}
.github .repository {
  width: 100%;
  border: 1px solid #ddd;
  background-color: #ddd;
  border-radius: 3px;
  min-height: 243px;
  text-align: center;
  margin: 0 auto;
  display: grid;
  grid-row-gap: 1px;
  grid-template-columns: repeat(6, 1fr);
}
@media screen and (min-width: 768px) {
  .github .repository div + div {
    border-left: 1px solid #ddd;
  }
}
.github .repository > div {
  min-height: 130px;
  background-color: #fff;
  display: inline-flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-evenly;
}
.github .repository > div .show {
  font-size: 1.3rem;
}
.github .repository .repository-star {
  grid-column: 1 / 3;
}
@media screen and (max-width: 768px) {
  .github .repository .repository-star {
    grid-column: 1 / 7;
  }
}
.github .repository .repository-fork {
  grid-column: 3 / 5;
}
@media screen and (max-width: 768px) {
  .github .repository .repository-fork {
    grid-column: 1 / 7;
  }
}
.github .repository .repository-my {
  grid-column: 5 / 7;
}
@media screen and (max-width: 768px) {
  .github .repository .repository-my {
    grid-column: 1 / 7;
  }
}
.github .repository .repository-popular {
  grid-column: 1 / 4;
  border-left: 0;
}
@media screen and (max-width: 768px) {
  .github .repository .repository-popular {
    grid-column: 1 / 7;
  }
}
.github .repository .repository-oldest {
  grid-column: 4 / 7;
}
@media screen and (MAX-width: 768px) {
  .github .repository .repository-oldest {
    grid-column: 1 / 7;
  }
}
.github .repository .repository-chart {
  border-left: 0;
  grid-column: 1 / 7;
  display: flex;
  flex-direction: column;
  padding: 24px;
}
.github .repository .repository-contribute {
  border-left: 0;
  grid-column: 1 / 7;
  display: inline-flex;
  flex-direction: row;
  justify-content: center;
  flex-wrap: wrap;
  padding: 24px;
}
.github .repository .repository-contribute .title {
  flex: 0 0 auto;
  width: 100%;
}
.github .repository .repository-contribute .list {
  white-space: nowrap;
  border-radius: 6px;
  margin: 5px 8px;
  padding: 2px 8px;
  border: 1px solid #d9d9d9;
  background: #fafafa;
  font-size: 15px;
  color: #000;
}
@media screen and (max-width: 768px) {
  .github .repository .repository-contribute .list {
    width: 100%;
  }
}
.github .repository .repository-contribute .list:hover {
  color: rgba(0, 0, 0, 0.65);
  text-decoration: underline;
}
.github .following-container,
.github .followers-container {
  width: 100%;
  border: 1px solid #ddd;
  border-radius: 3px;
  min-height: 243px;
  text-align: center;
  margin: 0 auto;
  display: inline-flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: flex-start;
  padding: 5px;
}
.github .following-container .list,
.github .followers-container .list {
  width: 100px;
  flex: 0 0 79px;
  margin: 9px;
  overflow-x: hidden;
  display: inline-flex;
  flex-direction: column;
}
@media screen and (max-width: 768px) {
  .github .following-container .list,
  .github .followers-container .list {
    flex: 0 0 calc(33%  - 17px);
  }
}
.github .following-container .list img,
.github .followers-container .list img {
  width: 80px;
  height: 80px;
  border-radius: 50%;
  border: 2px solid #a0dc70;
  padding: 1px;
}
.github .following-container .list .name,
.github .followers-container .list .name {
  white-space: nowrap;
  font-size: 12px;
}
